import React, { useState } from "react";
import { destroyCookie } from "nookies";
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js'


const CheckoutForm = (props) => {
    const [checkoutError, setCheckoutError] = useState();
    const [checkoutSuccess, setCheckoutSuccess] = useState();
    const stripe = useStripe();
    const elements = useElements();

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (!stripe || !elements) {

            return;
        }
        const cardElement = elements.getElement(CardElement);
        const { error, paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card: cardElement,
        });

        if (error) {
            setCheckoutError(err.message);
        } else {
            setCheckoutSuccess(true);
            destroyCookie(null, "paymentIntentId");
        }
    };

    const CARD_OPTIONS = {
        iconStyle: 'solid',
        style: {
          base: {
            border: '1px solid #ddd',
            iconColor: '#ff1721',
            color: '#111',
            fontSize: '16px',
            fontSmoothing: 'antialiased',
            ':-webkit-autofill': {color: '#ff1721'},
            '::placeholder': {color: '#111'},
          },
          invalid: {
            iconColor: '#ffc7ee',
            color: '#ffc7ee',
          },
        },
      };

    if (checkoutSuccess) return <p>Payment successful!</p>;

    return (
        <form onSubmit={handleSubmit}>
            <h4 className='final-sum'>Platit {props.sum} Kƒç</h4>
            <CardElement options={CARD_OPTIONS} />
            <a onClick={() => stepik()} href="#" className="af-class-button-2 af-class-order-btn-1 w-button continue-btn">
                        Pay
                      </a>
            <button type="submit" disabled={!stripe}>

        </button>
        {checkoutError && <span style={{ color: "red" }}>{checkoutError}</span>}
        </form>
    );
};


export default CheckoutForm
