import { useRouter } from "next/router";
import React, {useEffect, useState } from 'react';
import axios from 'axios';

export default () => {
  
  const router = useRouter();

  const [state, setState] = useState({
    data: null
  });

  const getData = async (id) =>{
    let res = await axios.get('http://localhost:1337/products?slug='+ id);
    setState({...state, data: res.data });
  }

  if(router.query.id && !state.data){
    getData(router.query.id);
  }

  if (state.data) {
    
    return (
      <>
        <div className="af-class-section">
          <div className="af-class-wrap">
            <div className="af-class-_6-col">
              <div data-animation="slide" className="af-class-slider-2 af-class-ecommerce w-slider product-slider">
                <SliderProduct img={state.data.Images}/>
              </div> 
            </div>
            <div className="af-class-_6-col af-class-_6-col-last">
              <div className="af-class-products1-content-wrap">
                <div className="af-class-products1-desc-wrap">
                  <h3>METALTEC-1 zbr/aně<br /></h3>
                  <p className="af-class-text-16 af-class-text-16-60">100 ml</p>
                </div>
                <div className="af-class-products1-price-wrap">
                  <h2 className="af-class-products1-price-headline">389,00 Kč<br /></h2>
                </div>
                <div className="af-class-products1-actions-wrap"><a href="#" className="af-class-btn w-button">Přidat do košíku</a></div>
                <div className="af-class-products1-details-wrap">
                  <p className="af-class-text-14 af-class-text-14-60">Nejnovější generace <strong>kondicionéru </strong>kovu snižujícího tření. Vědecky propracovaná ochrana všech kovových povrchů pomoci syntetického mazacího prostředku využívajícího molekulovou vazbu.<br /><br />METALTEC-1 &nbsp;je zcela nová technologie na redukování tření a tepla ve všech aplikacích kde dochází ke styku a tření kov – kov.Určen pro vnitřní a vnější ošetření při údržbě ​a ochraně střelných zbr/aní a zbr/aňových systémů všeho druhu. Ideální pro kovové součásti střelných zbr/aní. <br /><br />Molekulárně se naváže pouze na kov. Účinně chrání proti korozi, zvyšuje životnost dílů a zároveň snižuje opotřebení, odpuzuje vodu. <br /><br />Vhodný také pro užití v domácnosti - např. pro ošetření zámků a kovového nářadí.​POUŽITÍ: Při standardním ošetření zbr/aně naneste tenkou vrstvu na vnitřní a vnější plochy včetně hlavně a všech mechanismů. <br /><br />Dostatečná je velmi tenká vrstva. Po vypálení potřebného množství dávek pro zahřátí zbr/aně, je možné METALTEC-1 otřít suchým hadříkem ze zbr/aně, aniž by se ztratil lublikační efekt. <br /><br />Nechte velmi tenkou vrstvu METALTEC-1 na všech kovových součástech pro ochranu během dlouhodobého skladování. METALTEC-1 nepotřebuje před použitím zbr/aně dekonzervaci.<br /><br /><strong>Upozornění:<br />‍</strong>METALTEC neobsahuje látky vedené v nařízení vlády číslo 10/1999 Sb. (jedy a zvláště nebezpečné jedy). Veškeré hodnoty odpovídají Bezpečnostnímu listu a Prohlášení o shodě vydané firmou BOHEMIA PETROL ENERGY, s.r.o. Česká republika.Uchovávejte mimo dosah dětí!</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }else{
    return(<h3>Loading...</h3>)
    
  }
};
