import { useRouter } from "next/router";
import React, { useState } from 'react';
import SliderProduct from '../../components/sliders/slider-product.js'
import axios from 'axios';
import RichText from './components/richText'

export default () => {

  const router = useRouter();

  const [state, setState] = useState({
    data: null
  });

  const getData = async (id) => {
    let res = await axios.get('http://localhost:1337/products?slug=' + id);
    setState({ ...state, data: res.data[0] });
  }

  if (router.query.id && !state.data) {
    getData(router.query.id);
  }

  if (state.data) {
    console.log(state.data)
    return (
      <>
        <div className="af-class-section">
          <style jsx>
            {` 
                .af-class-wrap{
                  display: flex;
                  width: auto;
                  max-width: 1110px;
                  margin-right: auto;
                  margin-left: auto;
                  padding-top: 100px;
                  padding-bottom: 100px;
                }

                .af-class-_6-col {
                  position: relative;
                  top: 0px;
                  display: flex;
                  width: 50%;
                  height: 100%;
                  margin-right: 20px;
                  flex-direction: row;
                  justify-content: center;
                }
                .af-class-_6-col-last{
                  min-height: 600px;
                }

              .product-slider{
                position: sticky;
                top: 100px;
                right: auto;
                width: 100%;
                height: 500px;
              }

                @media screen and (max-width: 991px){
                  .af-class-wrap{
                    width: auto;
                    max-width: 1110px;
                    padding-right: 10vw;
                    padding-left: 10vw;
                    flex-direction: column;
                  }
                  .af-class-_6-col {
                    display: flex;
                    width: 100%;
                    min-height: auto;
                    margin-right: 0px;
                    margin-bottom: 20px;
                    align-self: center;
                    order: -1;
                    flex-grow: 0;
                    flex-shrink: 1;
                    flex-basis: auto;
                  }
                  .product-slider{
                    position: relative;
                    top: 0px;
                    width: 80%;
                    margin-top: 20px;
                    margin-bottom: 20px;
                  }
                }
                @media screen and (max-width: 767px){
                  .af-class-wrap{
                    width: 100%;
                    padding-right: 20px;
                    padding-left: 20px;
                  }
                  .af-class-_6-col{
                    margin-right: 0px;
                  }
                  .product-slider{
                    width: 90%;
                    height: 319px;
                  }
                }
                @media screen and (max-width: 479px){
                  .af-class-wrap{
                    padding-right: 5vw;
                    padding-left: 5vw;
                  }
                }
            `}
          </style>
          <div className="af-class-wrap">
            <div className="af-class-_6-col">
              <div data-animation="slide" className="af-class-slider-2 af-class-ecommerce w-slider product-slider">
                <SliderProduct img={state.data.Images} />
              </div>
            </div>
            <div className="af-class-_6-col af-class-_6-col-last">
              <div className="af-class-products1-content-wrap">
                <div className="af-class-products1-desc-wrap">
                  <h3>{state.data.Name}<br /></h3>
                  <p className="af-class-text-16 af-class-text-16-60">{state.data.Features}</p>
                </div>
                <div className="af-class-products1-price-wrap">
                  <h2 className="af-class-products1-price-headline">{state.data.Price} Kč<br /></h2>
                </div>
                <div className="af-class-products1-actions-wrap"><a href="#" className="af-class-btn w-button">Přidat do košíku</a></div>
                <div className="af-class-products1-details-wrap">
                  <p className="af-class-text-14 af-class-text-14-60">
                    <RichText data={state.data.Description} />
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  } else {
    return (<h3>Loading...</h3>)

  }
};
