import { useRouter } from "next/router";
import $ from 'jquery'
import React, { useState } from 'react';
import SliderProduct from '../../components/sliders/slider-product.js'
import axios from 'axios';
import RichText from './components/richText'
import NumericInput from 'react-numeric-input';



export default () => {

  const router = useRouter();

  const [state, setState] = useState({
    data: null,
    amount: 1
  });

  const getData = async (id) => {
    let res = await axios.get('http://localhost:1337/products?slug=' + id);
    setState({ ...state, data: res.data[0] });
  }

  if (router.query.id && !state.data) {
    getData(router.query.id);
  }

  const addToCart = (id) =>{
    $('.af-class-btn').toggleClass('active');
    $('.cart-btn-text-1').fadeToggle(0);
    $('.cart-btn-text-2').fadeToggle(0);
    let amount = state.amount;
    let products = [];
    let unic = true;
    if(localStorage.getItem('products')){
        products = JSON.parse(localStorage.getItem('products'));
        console.log(products)
    }
    products.forEach(el => {
      console.log(products);
      if(el.id == id){
        el.amount += amount;
        unic = false;
      }
    })
    if (unic) products.push({'id' : id,'amount' : amount});
    localStorage.setItem('products', JSON.stringify(products));
  }

  const amountChange = (num) =>{
    setState({ ...state, amount: num });
  }

  

  if (state.data) {
    console.log(state.data)
    return (
      <>
        <div className="af-class-section">
          <style jsx>
            {` 


              .cart-btn-text-2{
                width: 100%;
                display: none;
              }
                .af-class-wrap{
                  display: flex;
                  width: auto;
                  max-width: 1110px;
                  margin-right: auto;
                  margin-left: auto;
                  padding-top: 100px;
                  padding-bottom: 100px;
                  align-items: stretch;
                }

                .af-class-_6-col {
                  position: relative;
                  top: 0px;
                  display: flex;
                  width: 50%;
                  margin-right: 20px;
                  flex-direction: row;
                  justify-content: center;
                }
                .af-class-_6-col-last{
                  min-height: 600px;
                }

              .product-slider{
                position: sticky;
                top: 100px;
                border-radius: 10px;
                right: auto;
                width: 90%;
                height: 500px;
              }

                @media screen and (max-width: 991px){
                  .af-class-wrap{
                    width: auto;
                    max-width: 1110px;
                    padding-right: 10vw;
                    padding-left: 10vw;
                    flex-direction: column;
                  }
                  .af-class-_6-col {
                    display: flex;
                    width: 100%;
                    min-height: auto;
                    margin-right: 0px;
                    margin-bottom: 20px;
                    align-self: center;
                    order: -1;
                    flex-grow: 0;
                    flex-shrink: 1;
                    flex-basis: auto;
                  }
                  .product-slider{
                    position: relative;
                    top: 0px;
                    width: 80%;
                    margin-top: 20px;
                    margin-bottom: 20px;
                  }
                }
                @media screen and (max-width: 767px){
                  .af-class-wrap{
                    width: 100%;
                    padding-right: 20px;
                    padding-left: 20px;
                  }
                  .af-class-_6-col{
                    margin-right: 0px;
                  }
                  .product-slider{
                    width: 90%;
                    height: 319px;
                  }
                }
                @media screen and (max-width: 479px){
                  .af-class-wrap{
                    padding-right: 5vw;
                    padding-left: 5vw;
                  }
                  .af-class-btn{
                    display: block;
                    text-align: center;
                  }
                }
                .af-class-btn.active{
                  color: #ff373a;
                  border: 1px solid #ff373a;
                  background-color: #ff373a00;
                }
                .af-class-btn {
                    position: relative;
                    display: inline-block;
                    height: 35px;
                    margin-top: 25px;
                    margin-bottom: 50px;
                    padding-top: 5px;
                    padding-right: 25px;
                    padding-bottom: 5px;
                    padding-left: 25px;
                    float: none;
                    border-top-left-radius: 4px;
                    border-top-right-radius: 4px;
                    border-bottom-left-radius: 4px;
                    border-bottom-right-radius: 4px;
                    background-color: hsla(357.41379310344826, 100.00%, 54.51%, 1.00);
                    transition-property: color, box-shadow, background-color, transform;
                    transition-duration: 200ms, 200ms, 200ms, 400ms;
                    transition-timing-function: ease, ease, ease, ease;
                    color: white;
                    line-height: 24px;
                    text-align: center;
                  }
                  .af-class-products1-price-headline{
                    font-size: 18px;
                    font-weight: 400;
                    position: relative;
                    top: -15px;
                  }
                  
                  .af-class-btn:hover {
                    border-top-color: #0c6eb8;
                    border-right-color: #0c6eb8;
                    border-bottom-color: #0c6eb8;
                    border-left-color: #0c6eb8;
                    background-color: #ff373a;
                    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
                    transform: translate(0px, -2px);
                    color: white;
                  }
                  
                  .af-class-btn:active {
                    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.1);
                    transform: translate(0px, -1px);
                  }
                }
            `}
          </style>
          <div className="af-class-wrap">
            <div className="af-class-_6-col">
              <div data-animation="slide" className="af-class-slider-2 af-class-ecommerce w-slider product-slider">
                <SliderProduct img={state.data.Images} />
              </div>
            </div>
            <div className="af-class-_6-col af-class-_6-col-last">
              <div className="af-class-products1-content-wrap">
                <div className="af-class-products1-desc-wrap">
                  <h3>{state.data.Name}<br /></h3>
                  <p className="af-class-text-16 af-class-text-16-60">{state.data.Features}</p>
                </div>
                <div className="af-class-products1-price-wrap">
                  <h2 className="af-class-products1-price-headline">{state.amount * state.data.Price} Kč<br /></h2>
                  <NumericInput onChange={(num)=>amountChange(num)} className='numeric-input' min={1} max={10} value={state.amount}/>
                </div>
                <div onClick={()=> addToCart(state.data.slug)} className="af-class-products1-actions-wrap">
                  <a href="#" className="af-class-btn w-button">
                    
                    <div className='cart-btn-text-1'>
                      Přidat do košíku
                    </div>
                    <div className='cart-btn-text-2'>
                      Odebrat z košíku
                    </div>
                    </a>
                  </div>
                <div className="af-class-products1-details-wrap">
                  <p className="af-class-text-14 af-class-text-14-60">
                    <RichText data={state.data.Description} />
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  } else {
    return (<h3>Loading...</h3>)

  }
};
