import React, { useState } from "react";
import { destroyCookie } from "nookies";
import {CardElement, useStripe, useElements} from '@stripe/react-stripe-js'

const CheckoutForm = ({ paymentIntent }) => {
    const [checkoutError, setCheckoutError] = useState();
    const [checkoutSuccess, setCheckoutSuccess] = useState();

    const stripe = useStripe();
    const elements = useElements();

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            const {paymentResult, error} = await stripe.confirmCardPayment(
               paymentIntent.client_secret,
                {
                    payment_method: {
                        card: elements.getElement(CardElement)
                    }
                },
                {handleActions: false}
              );
            
              console.log(paymentResult);

            if (error) throw new Error(error.message);

            if (paymentResult.status === "requires_action") {
                // Let Stripe.js handle the rest of the payment flow.
                const {error} = await stripe.confirmCardPayment(paymentIntent.client_secret);
                if (error) {
                  // The payment failed -- ask your customer for a new payment method.
                  console.log(error);
                } else {
                  // The payment has succeeded.
                  alert('success')
                }
              } else {
                // The payment has succeeded.
                
              }

            console.log(paymentResult)
            // if (status === "succeeded") {
            //     setCheckoutSuccess(true);
            //     destroyCookie(null, "paymentIntentId");
            // }
        } catch (err) {
            console.log(err);
            setCheckoutError(err.message);
        }
    }

    if (checkoutSuccess) return <p>Payment successful!</p>;

    return (
        <form onSubmit={(e)=>handleSubmit(e)}>
            <CardElement />

            <button type="submit" disabled={!stripe}>
                Pay now
            </button>
            {checkoutError && <span style={{ color: "red" }}>{checkoutError}</span>}
        </form>
    );
}

export default CheckoutForm
