import gql from "graphql-tag";
import Link from "next/link";
import { graphql } from "react-apollo";


const ProductList = (
  { data: { loading, error, items }, search },
  req
) => {
  if (error) return "Error loading restaurants";


  if (items && items.length) {
    //searchQuery
    const searchQuery = items.filter(query =>
      query.name.toLowerCase().includes(search)
    );
    if (searchQuery.length != 0) {
      return (
        <div>
          <div className="h-100">
            {searchQuery.map(res => (
    
                        <div className="af-class-product-card">
                        <div className="af-class-product-card-image af-class-product-card-2"><img src={`http://localhost:1337${res.image.url}`} loading="lazy" alt className="af-class-image-4" /></div>
                        <div className="af-class-product-card-description">
                          <div className="af-class-div-block-38">
                          <h3 className="af-class-product-name-title">{res.name}<br /></h3>
                          </div>
                          <div className="af-class-price-wrapper">
                            <h2 className="af-class-product-price">{res.price} Kƒç<br /></h2>
                          </div>
                        </div>
                      </div>
                  
 
            ))}
          </div>
        </div>
      );
    } else {
      return <h1>No Products Found</h1>;
    }
  }
  return <h1>Loading...</h1>;
};

const query = gql`
  {
    products {
      _id
      name
      price
      image {
        url
      }
    }
  }
`;
export default graphql(query, {
  props: ({ data }) => ({
    data
  })
})(ProductList);
