$(document).ready(()=>{
    $('.af-class-reseni-item').mouseenter((e)=>{
        let btns = $(e.target).find(".af-class-reseni-btn");
        gsap.to(btns, 0.3, {y: -75, opacity: 1,  ease: Power4.easeInOut})
    })
    $('.af-class-reseni-item').mouseleave((e)=>{
        gsap.to('.af-class-reseni-btn', 0.3, {y: 0, opacity: 0, ease: Power4.easeInOut})
    })
})
