import Link from "next/link";
import { useQuery } from '@apollo/react-hooks';
import { gql } from '@apollo/client';

const ProductList = () => {

  const { loading, error, data } = useQuery(gql`
  query Products {
    products{
      id
      Name
      Price
        Images{
          url
        }
    }
  }
`);
  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;
  console.log(data);
  if (data) {
    
    return (
      <div>
        <div className="h-100">
          {data.products.map(res => (
            <div className="af-class-product-card">
              {/* <div className="af-class-product-card-image af-class-product-card-2"><img src={`http://localhost:1337${res.ima}`} loading="lazy" alt className="af-class-image-4" /></div> */}
              <div className="af-class-product-card-description">
                <div className="af-class-div-block-38">
                  <h3 className="af-class-product-name-title">{res.name}<br /></h3>
                </div>
                <div className="af-class-price-wrapper">
                  <h2 className="af-class-product-price">{res.price} Kƒç<br /></h2>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  } else {
    return <h1>No Products Found</h1>;
  }
}

export default ProductList;
