import React, { useEffect } from 'react';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

import dynamic from "next/dynamic";

const Slick = dynamic(
  () => {
    return import("react-slick");
  },
  { ssr: false }
);


const SliderProduct = () =>{

    const settings = {
        customPaging: function(i) {
          return (
            <a>
              ССылка
            </a>
          );
        },
        dots: true,
        dotsClass: "slick-dots slick-thumb",
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
      };
    
return(
    <div className="w-slider-mask">
    <style jsx>
            {` 
                .w-slide{
                    height: 500px;
                }
                .w-slider-mask {
                  width: 100%;
                }
            `}
      </style>
        <Slick {...settings}>
            {props.img.map((item)=>{
                return(
                    <div className='w-slide-product'>
                        <img src={`http://localhost:1337${item}`} loading="lazy" alt className="product-slider-img" />
                    </div>
                )
            })}
        </Slick>
    </div>
)

}

export default SliderProduct;
